import axios from 'axios'

class AddTodo extends Component {

    state = {
        content: ''
    }
    handleChange = (e) => {
        this.setState({
            content: e.target.value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault();

    const taskname = {
            id:Math.random(),
            content: this.state.content
          };
