         .then(res => {
        console.log(res);
        console.log(res.data);
        })

    this.props.addTodo(this.state);
    this.setState ({
            content: ''
        })    
    }

    render () {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label>Add new todo:</label>
                    <input type="text" onChange={this.handleChange} value={this.state.content}/>
                </form>
            </div>
            )
    }
        
    
}

export default AddTodo
